sys_script_client.script:
function onLoad() {
   //Type appropriate comment here, and begin script below

   	   var requestType = g_form.getValue('u_request_type');
      		if(requestType !== 'hr'){
			g_form.removeOption('u_what_needed','hr1');
			g_form.removeOption('u_what_needed','hr2');
			g_form.removeOption('u_what_needed','hr3');
			g_form.removeOption('u_what_needed','other');

      }
	   		if(requestType !== 'facilities'){
			g_form.removeOption('u_what_needed','facilities1');
			g_form.removeOption('u_what_needed','facilities2');
			g_form.removeOption('u_what_needed','other');
		}
	   		if(requestType !== 'legal'){
			g_form.removeOption('u_what_needed','legal1');
			g_form.removeOption('u_what_needed','legal2');
			g_form.removeOption('u_what_needed','other');
		}
}
